import axios from "axios";
import React, {useEffect} from "react";
import {useState} from "react";
import {useNavigate, useParams} from "react-router-dom";

export function Profile(){
    const [user, setUser] = useState([])
    const navigate = useNavigate()
    const params = useParams()

    const Logout = (e) =>{
        e.preventDefault()
        console.log("logout")
        localStorage.removeItem('Token');
        navigate('/')
    }

    const getData = () => {
        let token=JSON.parse(window.localStorage.getItem('Token'))
        console.log(params.userid)
        console.log(token)

        axios.get("http://localhost:9087/customer/"+params.userid ,{
            headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}
        })
            .then(response => {
                console.log(response.data)
                setUser(response.data)
                console.log(user)
            })
            .catch(err => {
                console.log('error')
                console.log(err)
            })
    }

    useEffect(()=>{
        getData()

    },[])

    return(
        <>
            <img align='top' style = {{right:'0px' , height: '200px' , width: '200px'}} alt={user.name} src={`data:image/jpeg;base64,${user.image}`}  />
            <p align="right" style={{fontStyle:'oblique'}}>
                <h1>{user.name}</h1> <br/><br/>
                <h1>{user.email}</h1><br/><br/>
                <h1>{user.phone}</h1><br/><br/>
            </p>

            <button className="logoutLblPos" onClick={Logout}>
                LOG OUT
            </button>

            <div className="card">
                <img src="/w3images/team2.jpg" alt="John" style="width:100%"/>
                    <h1>John Doe</h1>
                    <p className="title">CEO & Founder, Example</p>
                    <p>Harvard University</p>
                    <p>
                        <button>Contact</button>
                    </p>
            </div>
        </>

    )

}
