import React, {useState} from "react";
import {useNavigate, useParams} from "react-router-dom";
import axios from "axios";
import {ViewItemsToCustomer} from "./ViewItemsToCustomer";


export function SearchShops(props){
    const params= useParams()
    const [loc, setLocation] = useState()
    const [isResponse, setResponse] = useState(false)
    const [shops, setShops] = useState([])
    const [isItem, setIsItem] = useState(false)
    const [items, setItems] = useState([])
    const navigate = useNavigate()
    const takeToItems = (e) =>{
        e.preventDefault()
        console.log(e.target.id)
        navigate("/viewItemsToCustomer/"+params.userid+"/"+e.target.id)
    }

    const handleResponse= (e) =>{
        e.preventDefault()
        console.log(props.type)
        console.log(loc.type)
        let token=JSON.parse(window.localStorage.getItem('Token'))
        axios.post("http://localhost:9087/customer/viewshops",loc, {
            headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}
        })
            .then(response => {
                console.log(response)
                console.log(response.data)
                if(response.data !== "")
                {
                    setResponse(true)
                    setShops(response.data)
                    navigate("")
                }
            })
            .catch(err => {
                console.log(err)
            })
        e.preventDefault()
    }
    return(
        <>
            <div style={{display:'flex' ,flexDirection: 'column'}}>
                <form   onSubmit={handleResponse}>
                <div>
                    <label>Choose location : </label>
                </div><br/>
                <div><input type="text" placeholder="Enter location" onChange={(e)=>{setLocation({...loc, location:e.target.value, type:props.type})}}/>
                </div><br/>
                <div><input type="submit" value="Submit" onSubmit={handleResponse}/></div>
                </form>

            <div style={{display:'flex' ,flexDirection: 'row'}}>
            {
                shops.length > 0 ? shops.map((item) =>

                                            <button style={{width:'500px'}} className="btn_class" id={item.id} onClick={takeToItems}>
                                                <img align='top' style = {{right:'0px' , height: '200px' , width: '200px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  /><br/><br/><br/><br/>
                                                <p align="center" style={{fontSize:'25px',fontFamily:'Courier'}}>
                                                    <br/><label style={{fontColor:'black'}}>Store Name:</label> {item.storeName}<br/><br/>
                                                    <label style={{fontColor:'black'}}>Location:</label> {item.storeLoc}<br/><br/>
                                                    <label style={{fontColor:'black'}}>Quantity: </label> {item.qty}<br/>
                                                </p><br/>
                                            </button>

                                )
                                :""
            }
            </div>
            </div><br/><br/>

        </>
    )
}
