import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import axios from "axios";
export function ViewItemsToCustomer(){
    const params = useParams()
    const [inventory, setInventory]= useState([])
    const [items , setItems] = useState()
    const [selectedItems, setSelected] = useState({
        shopId: '',
        itemId:'',
        qty: ''
    })

    const addItemToCart=(e)=>{
        console.log(items)//quantity
        console.log(e.target.name)//itemid
        console.log(e.target.id)//shop
        setSelected({...selectedItems , shopId: e.target.id})
        setSelected( {...selectedItems, itemId: e.target.name})
        setSelected( {...selectedItems, qty: items})

        console.log(selectedItems)

        axios.put("http://localhost:9087/"+ params.userid +"/addtocart1" , selectedItems )
            .then(response => {
                console.log(response)
            })
            .catch(err => {
                console.log(err)
            })
    }
    const getData = () =>{
        axios.get("http://localhost:9087/inventory/"+params.shopid)
            .then(response => {
                console.log(response)
                setInventory(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    return(
        <div>
            <div className = 'text-center' >
                <h1>Products</h1>
            </div>
            {
                inventory.length > 0
                    ? inventory.map((item) =>
                        <div>
                            <button className="btn_cls">
                                <img align='left' style = {{right:'0px' , height: '150px' , width: '150px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  />
                                <h3 style = {{right:'0px', textAlign:'center', fontSize:'10px' ,color:'black'}}>
                                    <p>Name: {item.item_name}<br/></p>
                                    <p>Description: {item.description} <br/></p>
                                    <p>Price: {item.price}</p></h3>
                            </button>    <label>Quantity: </label><input type="number" onChange={(e)=>{
                                setSelected({...selectedItems, qty: e.target.value})
                        }}
                        />                     <button name={item.id} id={item.partner.id} onClick={addItemToCart}>Add to Cart</button>      <button>Buy now</button><br/><br/>
                        </div>

                    )
                    : "No products"
            }

        </div>
    )

}
