import React, {useEffect, useState} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";
export  function BuyNow(){
    const params = useParams()
    const [item, setItem]=useState([])
    const getData = () =>{
        axios.get("http://localhost:9087/item/"+params.itemid)
            .then(response => {
                console.log(response)
                setItem(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    return(
        <>
            <img align='left' style = {{margin:'0px', height: '500px' , width: '500px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  />
            <h1 style = {{right:'0px', textAlign:'center', fontSize:'10px' ,color:'black'}}>
                Name: {item.item_name}<br/>
                Description: {item.description} <br/>
                Price: {item.price}
            </h1>

        </>
    )
}
