import Login from "../Login";
import {useNavigate, useParams} from "react-router-dom";
import axios from "axios";
import React, {useEffect, useState} from "react";
import {toast} from "react-toastify";

export function Carts(){
    const params=useParams()
    const navigate = useNavigate()
    const [cart, setCart] = useState([])
    const getData = () =>{
        axios.get("http://localhost:9087/viewcart/"+params.userid)
            .then(response => {
                console.log(response)
                setCart(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    const ordernow  = (e) => {
        e.preventDefault()
      console.log("reached")
        navigate("/ordernow/"+params.userid)
    }
    return(
        <div>
                    {
                        cart.length>0
                            ? cart.map((item)=>
                                <div>
                                    <button className="btn_class">
                                        <img align='left' style = {{right:'0px' , height: '150px' , width: '150px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  />
                                        <h3>
                                            <p>Name: {item.item_name}<br/></p>
                                            <p>Description: {item.description} <br/></p>
                                            <p>Price: {item.price}</p>
                                            <p>Available Quantity: {item.qty}</p>
                                        </h3>

                                    </button><br/><br/>
                                </div>

                            )
                            :""
                    }
        </div>

    )
}
