import axios from "axios";
import React, {useEffect} from "@types/react";
import {useNavigate, useParams} from "react-router-dom";
import {useState} from "react";

export function Orders(){
    const params = useParams()
    const [orders, setOrders] = useState()
    const navigate = useNavigate
    const getData = () =>{
        let token=JSON.parse(window.localStorage.getItem('Token'))
        axios.get("http://localhost:9087/customer/orders/"+params.userid, {headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}})
            .then(response => {
                console.log(response)
                if(response.data !== "")
                {
                    setOrders(response.data)
                    window.confirm("Order confirmed. Wait for our delivery boy, to pick it up soon.")
                    //navigate('/')
                }
                else
                    window.alert("Could not place your order at the moment. Sorry!!!")
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    return(
        <>

            {
                orders.length > 0 ? orders.map((item) =>
                        <div>

                        <span style={{ marginLeft: "60px" }}>
                            <label><img align='left' style={{right: '0px', height: '200px', width: '200px'}}
                                        alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}/></label><br/>
                            <label>Name:</label> {item.item_name}<br/><br/>
                            <label>Price:</label> {item.price}<br/><br/>
                            <label style={{font:'1px'}}>Unit:</label> {item.unit}<br/><br/>
                            <label>Quantity: </label>  <input style={{width: '120px'}}
                                                              placeholder="Quantity"
                                                              type="number"
                                                              onChange={(e) => {
                                                                  setSelected({
                                                                      ...selectedItems,
                                                                      qty: e.target.value
                                                                  })
                                                              }}/><br/>

                            <br/><br/><br/>
                            <button style={{marginRight: '6px'}} className="desc" name={item.id}
                                    id={item.partner.id} onClick={addItemToCart}>Add to Cart
                                </button><br/><br/>
                            <button style={{marginRight: '6px'}} className="desc" name={item.id}
                                    id={item.partner.id} onClick={goToBuyPage}>Buy now
                                </button>
                                <br/>
                        </span>

                            <br/><br/>
                        </div>
                    )
                    : ""
            }

        </>
    )
}
