import React, {useEffect, useState} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";
export  function BuyNow(){
    const params = useParams()
    const [item, setItem]=useState([])
    const getData = () =>{
        console.log(params.itemid)
        axios.get("http://localhost:9087/item/"+params.itemid)
            .then(response => {
                console.log(response)
                setItem(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    return(
        <>
            <img align='left' style = {{margin:'0px', height: '300px' , width: '300px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  /><br/>
            <form align='right'>
                <button className="desc">Name: {item.item_name}</button><br/><br/>
                <button className="desc">Price: {item.price}</button><br/><br/>
                <button className="desc">Ordered Quantity: {params.qty} </button><br/><br/>
                <button className="desc">Order Total: {params.qty * item.price} </button><br/><br/>
            </form>

        </>
    )
}
