import React, {useState} from "react";
import {Card, CardBody, CardImg, CardSubtitle, CardText, CardTitle} from "reactstrap";
export function DisplayImage(item){

    return(
        <Card className = 'mt-5' >
            <CardImg top width="100%" src = {`data:image/jpeg;base64,${item.image}`} alt="Card image cap" />
            <CardBody className="text-center">
                <CardTitle className="font-weight-bold">{item.item_name}</CardTitle>
                <CardSubtitle>{item.price} <br/>
                    {/*<br/> {product.art_id.name}*/}
                </CardSubtitle>
                <CardText>{item.description}</CardText>
            </CardBody>
        </Card>
    );


}
