import Login from "../Login";
import {useNavigate, useParams} from "react-router-dom";
import axios from "axios";
import React, {useEffect, useState} from "react";
import {toast} from "react-toastify";

export function Carts(){
    const params=useParams()
    const navigate = useNavigate()
    const [cart, setCart] = useState([])
    const getData = () =>{
        axios.get("http://localhost:9087/viewcart/"+params.userid)
            .then(response => {
                console.log(response)
                setCart(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    const ordernow  = (e) => {
        e.preventDefault()
      console.log("reached")
        navigate("/ordernow/"+params.userid)
    }
    return(
        <div>
                    {
                        cart.length>0
                            ? cart.map((item)=>
                                <div>
                                    <button className="btn_class">
                                        <img align='left' style = {{right:'0px' , height: '150px' , width: '150px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  /><br/><br/><br/><br/>
                                        <h1 align="right" style={{fontSize:'12px'}}>
                                            <button >Name: {item.item_name}</button><br/><br/>
                                            <button >Price: {item.price}</button><br/><br/>
                                            <button >Quantity: {item.qty}</button><br/><br/>
                                        </h1><br/><br/><br/>
                                        <button style={{height:'10px'}}>Description: {item.description}</button>
                                        <br/>

                                    </button><br/><br/>
                                </div>

                            )
                            :""
                    }
        </div>

    )
}
