import React, {useEffect, useState} from "react"
import {useParams} from "react-router-dom";
import axios from "axios";

export function BuyNow(){
    const params = useParams()
    const [item, setItem]=useState([])
    const [address, setAddress]=useState([])
    const [dropdown, setDropdown] = useState('Add Address')

    const getData = () =>{
        let token=JSON.parse(window.localStorage.getItem('Token'))
        console.log(params.itemid)
        axios.get("http://localhost:9087/customer/item/"+params.itemid , {
            headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}
        })
            .then(response => {
                console.log(response)
                setItem(response.data)
            })
            .catch(err => {
                console.log(err)
            })

        axios.get("http://localhost:9087/customer/viewaddress/"+params.userid, {
            headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}
        })
            .then(response => {
                console.log(response)
                setAddress(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])


    const submitResponse = (e) => {
        if(dropdown === "Add Address")
        {
            //open a html form to add address
        }
        else{

            //add data to order table
            //show message that you ordered
        }
    }

    return(
        <>
            <div><h1>Buy Now</h1><br/></div>
            <img align='left' style = {{height: '350px' , width: '350px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  /><br/><br/>

            <form  align='right' onSubmit={submitResponse}>
                <button style={{marginRight: '15px'}} className="buynow">Name: {item.item_name}</button><br/><br/>
                <button style={{marginRight: '15px'}} className="buynow">Price: {item.price}</button><br/><br/>
                <button style={{marginRight: '15px'}} className="buynow">Ordered Quantity: {params.qty} </button><br/><br/>
                <button style={{marginRight: '15px'}} className="buynow">Order Total: {params.qty * item.price} </button><br/><br/>
                <button style={{marginRight: '15px', width:"500px"}} className="buynow">Address:
                    <select style={{marginRight: '6px',marginLeft: '30px' , height:'50px', width:'260px'}} onChange={(e)=>{setDropdown(e.target.value)}}>
                        {
                            address.length >0 ? address.map((item) =>
                                <option value={item.pincode}>{item.name}, {item.housenumber} {item.area} , {item.city} {item.state} {item.pincode} {item.phone}</option>
                            ) : "add address"

                        }
                        <option value="Add Address"> <p style={{color:'#141823'}}>Add Address</p> </option>

                    </select>
                </button><br/><br/>

                <input type="submit" value="Submit" onChange={submitResponse}/>
            </form>

        </>
    )

}
