import React, {useEffect, useState} from "react"
import {useParams} from "react-router-dom";
import axios from "axios";

export function BuyNow(){
    const params = useParams()
    const [item, setItem]=useState([])
    const [dropdown, setDropdown] = useState('Choose Address')
    let address = []

    const getData = () =>{
        console.log("itemid "+params.itemid)

        let token=JSON.parse(window.localStorage.getItem('Token'))
        axios.get("http://localhost:9087/customer/item/"+params.itemid, {
            headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}
        })
            .then(response => {
                console.log(response)
                setItem(response.data)
            })
            .catch(err => {
                console.log(err)
            })


        axios.get("http://localhost:9087/customer/viewaddress/"+params.userid , {
            headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}})
            .then(res => {
                console.log(res)
                address = res.data
                console.log(address)
                console.log(address.length)
            })
            .catch(err => {
                console.log(err)
            })
    }


    const submitResponse = (e) => {
        if(dropdown === "Add Address")
        {
            //open a html form to add address
        }
        else{


            axios.get("http://localhost:9087/addOrder/"+params.userid+"/"+params.itemid+"/"+e.target.name+"/")
                .then(response => {
                    console.log(response)
                    console.log("added to cart successfully")
                })
                .catch(err => {
                    console.log(err)

                })
        }
    }


    useEffect(()=>{
        getData()
    },[])




    return(
        <>
            <img align='left' style = {{height: '250px' , width: '250px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  /><br/><br/>

            <form  align='right' onSubmit={submitResponse}>
                <label>Name: {item.item_name}</label><br/><br/>
                <label>Price: {item.price}</label><br/><br/>
                <label>Ordered Quantity: {params.qty} </label><br/><br/>
                <label>Order Total: {params.qty * item.price} </label><br/><br/>
                <label>Address:</label>
                {
                    address.length >0 ? address.map((item) =>
                        {
                            <h1>entered</h1>
                        })
                        :""
                }

            </form>

        </>
    )


}
