import React, {useEffect, useState} from "react";
import {useNavigate, useParams} from "react-router-dom";
import axios from "axios";
export function ViewItemsToCustomer(){
    const params = useParams()
    const [inventory, setInventory]= useState([])
    const [items , setItems] = useState()
    const [selectedItems, setSelected] = useState({
        qty: ''
    })

    const navigate = useNavigate()

    const addItemToCart=(e)=>{
        console.log(items)//quantity
        console.log(e.target.name)//itemid
        console.log(e.target.id)//shop
        setSelected( {...selectedItems, qty: items})

        let token=JSON.parse(window.localStorage.getItem('Token'))

        console.log(selectedItems)
        axios.get("http://localhost:9087/customer/addtocart/"+params.userid+"/"+e.target.name+"/"+selectedItems.qty, {
            headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}
        })
            .then(response => {
                console.log(response)
                console.log("added to cart successfully")
                if(response !== "")
                    window.confirm("Added item to cart successfully!!")
                else
                    window.alert("Could not add item to cart")
            })
            .catch(err => {
                console.log(err)

            })
    }

    const goToBuyPage=(e)=>{
        navigate("/buynow/"+params.userid+"/"+e.target.name+"/"+selectedItems.qty)
    }

    const getData = () =>{
        let token=JSON.parse(window.localStorage.getItem('Token'))
        axios.get("http://localhost:9087/customer/inventory/"+params.shopid, {headers: {'Authorization' : 'Bearer '+token,
            'Access-Control-Allow-Origin': '*'}})
            .then(response => {
                console.log(response)
                setInventory(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    return(
        <>

            {
                inventory.length > 0 ? inventory.map((item) =>
                    <div>
                        <snap style={{ marginLeft: "60px" }}>
                            <>
                        <button className="btn_class_view">
                            <img align='top' style = {{right:'0px' , height: '200px' , width: '200px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  /><br/><br/><br/><br/>
                            <h1 align="center" style={{fontSize:'25px'}}>
                                <br/><label style={{fontColor:'black'}}>Name:</label> {item.item_name}<br/><br/>
                                <label style={{fontColor:'black'}}>Price:</label> {item.price}<br/><br/>
                                <label style={{fontColor:'black'}}>Quantity: </label> <input style={{width: '120px'}}
                                                                                             placeholder="Quantity"
                                                                                             type="number"
                                                                                             onChange={(e) => {
                                                                                                 setSelected({
                                                                                                     ...selectedItems,
                                                                                                     qty: e.target.value
                                                                                                 })
                                                                                             }}/><br/>
                                <button className="desc" name={item.id}
                                        id={item.partner.id} onClick={addItemToCart}>Add to Cart
                                </button><br/><br/>
                                <button className="desc" name={item.id}
                                        id={item.partner.id} onClick={goToBuyPage}>Buy now
                                </button>
                            </h1><br/><br/><br/>

                        </button>
                            </>
                        </snap>
                    </div>
                )
                : ""
            }

        </>

    )

}
