import Login from "../Login";
import {useNavigate, useParams} from "react-router-dom";
import axios from "axios";
import React, {useEffect, useState} from "react";
import {toast} from "react-toastify";

export function Carts(){
    const params=useParams()
    const navigate = useNavigate()
    const [cart, setCart] = useState([])
    const getData = () =>{
        axios.get("http://localhost:9087/viewcart/"+params.userid)
            .then(response => {
                console.log(response)
                setCart(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    const ordernow  = (e) => {
        e.preventDefault()
      console.log("reached")
        navigate("/ordernow/"+params.userid)
    }
    return(
        <div>
                    {
                        cart.length>0
                            ? cart.map((item)=>
                                <div>
                                    <button className="btn_class">
                                        <img align='left' style = {{right:'0px' , height: '150px' , width: '150px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  />
                                        <label>Name: </label>            <input value={item.item_name} type="text"/><br/><br/><br/>
                                        <label>Description:</label>         <textarea>{item.description}</textarea><br/><br/><br/>
                                        <label>Price: </label>        <input value={item.price} type="text"/><br/><br/><br/>
                                        <label>Ordered Quantity: </label>         <input value={item.qty} type="text"/><br/><br/><br/>
                                        <label>Order Total : </label>         <input value={params.qty * item.price} type="text"/><br/><br/><br/>
                                    </button><br/><br/>
                                </div>

                            )
                            :""
                    }
        </div>

    )
}
