import React, {Component} from "react";
import axios from "axios";
import Register from "./Register";
class Login extends Component{
    constructor() {
        super();
        this.state = {
            username:'',
            password:'',
            type:''
        }
    }
    changeUsername = (event) => {
        this.setState({
            username: event.target.value
        })
    }
    changePassword = (event) => {
        this.setState({
            password: event.target.value
        })
    }
    changeType = (event) => {
        this.setState({
            type: event.target.value
        })
    }

    submitresponse = (event) =>{
        alert('login page created successfully')

        event.preventDefault()
    }

    register =(event) => {
        //alert('register')
        axios.post("http://localhost:9087/login" , this.state.username, this.state.password, this.state.type)
            .then()
            .catch()
    }

    render() {
        return (
            <div>
            <form onSubmit={this.submitresponse}>
                <label>Username: </label>
                <input type='text' value={this.state.username} onChange={this.changeUsername}/>

                <label>Password: </label>
                <input type='password' value={this.state.password} onChange={this.changePassword}/>

                <label>Type: </label>
                <select value={this.state.type} onChange={this.changeType}>
                    <option value='Cust'>Customer</option>
                    <option value='part'>Partner</option>
                    <option value='Del'>Delivery</option>
                </select>
                <input type='submit' value='Login' onChange={this.submitresponse}/>
            </form>
            <form onSubmit={this.register}>
                <input type='submit' value='Register' onChange={this.register}/>
            </form>
        </div>
        );
    }
}
export default Login
