import React, {useEffect, useState} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";
import {Container, Row} from "reactstrap";

export function Inventory(){
    const btn_cls = {
        cursor: 'pointer',
        border: '1px solid #111111',
        background: 'transparent',
        height: '200px',
        width: '400px',
        color: '#3498db',
        fontSize: '1.5em',
        boxShadow: '0 6px 6px rgba(0, 0, 0, 0.6)',
        textTransform: 'uppercase'
    }

    const params = useParams()
    const [inventory, setInventory]= useState([])

    const getData = () =>{
    axios.get("http://localhost:9087/inventory/"+params.userid)
        .then(response => {
            console.log(response)
            setInventory(response.data)
        })
        .catch(err => {
            console.log(err)
        })
}
    useEffect(()=>{
        getData()
    },[])

    return(
        <div>
            <div className = 'text-center' >
                <h1>Products</h1>
                {/* <p>list of products are given:</p> */}
            </div>
            <Container fluid>
                <Row>
                    {
                        inventory.length > 0
                            ? inventory.map((item) =>
                                <button className="btn_cls">
                                    <img alt="" src={`data:image/jpeg;base64,${item.image}`} className="photo"/>
                                    <br/>
                                    {item.item_name}<br/>
                                    {item.description} <br/>
                                    {item.price}</button>
                            )
                            : "No products"
                    }
                </Row>
            </Container>

        </div>
    )

}
