import React, {useState} from "react";
import {useNavigate} from "react-router-dom";
import axios from "axios";
import {ViewShops} from "./ViewShops";
import {Column, Container, FooterLink, Heading, Row} from "../Footer/FooterStyles";
import styled from "styled-components";
export function SearchShops(props){
    const [loc, setLocation] = useState()
    const [isResponse, setResponse] = useState(false)
    const [shops, setShops] = useState([])
    const navigate =useNavigate()
    const Box = styled.div`
  padding: 10px 20px;
  background: #0C797D;
  position: absolute;
  bottom: 20;
  marginRight: 0;
  left: 50;
  width: 40%;
  align: center
  @media (max-width: 1000px) {
    padding: 70px 80px;
  }
`;

    const handleResponse= (e) =>{
        e.preventDefault()
        console.log(props.type)
        console.log(loc.type)
        axios.post("http://localhost:9087/viewshops",loc)
            .then(response => {
                console.log(response)
                console.log(response.data)
                if(response.data !== "")
                {
                    setResponse(true)
                    setShops(response.data)
                }
            })
            .catch(err => {
                console.log(err)
            })
    }
    return(
        <div style={{left:'0px'}}>
        <form   onSubmit={handleResponse}>
            <div>
                <label>Choose location : </label>
            </div><br/>
            <div><input type="text" placeholder="Enter location" onChange={(e)=>{setLocation({...loc, location:e.target.value, type:props.type})}}/>
            </div><br/>
            <div><input type="submit" value="Submit" onSubmit={handleResponse}/></div>
        </form>
            <br/>
            {
                            shops.length > 0 ? shops.map((item) =>
                                    <Column>
                                        <Heading>{shops.storeName}</Heading>
                                        <FooterLink href="#">Vision</FooterLink>
                                        <FooterLink href="#">Testimonials</FooterLink>
                                    </Column>
                                )
                                :
                                <h1>NoShops</h1>
            }

        </div>
    )
}
