                            <label>Name:</label> {item.item_name}<br/><br/>
                            <label>Price:</label> {item.price}<br/><br/>
                            <label>Unit:</label> {item.price}<br/><br/>
                            <label>Quantity: </label>  <input style={{width: '120px'}}
                                                              placeholder="Quantity"
                                                              type="number"
                                                              onChange={(e) => {
                                                                  setSelected({
                                                                      ...selectedItems,
                                                                      qty: e.target.value
                                                                  })
                                                              }}/><br/>

                            <br/><br/><br/>
                            <button style={{marginRight: '6px'}} className="desc" name={item.id}
                                    id={item.partner.id} onClick={addItemToCart}>Add to Cart
                                </button><br/><br/>
                            <button style={{marginRight: '6px'}} className="desc" name={item.id}
                                    id={item.partner.id} onClick={goToBuyPage}>Buy now
                                </button>
                            <br/>
                        </span>
                        <br/><br/>
                    </div>
                )
                : ""
            }

