import axios from "axios";
import React, {useEffect} from "react";
import {useNavigate, useParams} from "react-router-dom";
import {useState} from "react";

export function Orders(){
    const params = useParams()
    const [orders, setOrders] = useState([])
    const navigate = useNavigate
    const getData = () =>{
        let token=JSON.parse(window.localStorage.getItem('Token'))
        axios.get("http://localhost:9087/customer/orders/"+params.userid, {headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}})
            .then(response => {
                console.log(response.data)
                setOrders(response.data)
                console.log(orders)
                if(response.data !== "")
                {
                    console.log("enterd if")
                    setOrders(response.data)
                    console.log("orders= ")
                    console.log(orders)

                    console.log(orders)
                    //window.confirm("Order confirmed. Wait for our delivery boy, to pick it up soon.")
                    //navigate('/')
                }
                //ndow.alert("Could not place your order at the moment. Sorry!!!")
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    return(
        <>
            {
                orders.length > 0 ? orders.map((item) =>
                        <div>

                        <span style={{ marginLeft: "60px" }}>
                            <label><img align='left' style={{right: '0px', height: '200px', width: '200px'}}
                                        alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}/></label><br/>
                            <label>Name:</label> {item.item_name}<br/><br/>
                            <label>Price:</label> {item.price}<br/><br/>
                            <label>Quantity: </label> {item.qty}<br/><br/>
                            <label>Status: </label>{item.status}<br/><br/>
                            <label>Delivery details: </label><br/>
                            <label>Name:</label>{item.delivery.name}<br/><br/>
                            <label>Phone</label>{item.delivery.phone}<br/><br/>

                        </span>
                        </div>
                    )
                    : ""
            }

        </>
    )
}
