import Login from "../Login";
import {useNavigate, useParams} from "react-router-dom";
import axios from "axios";
import React, {useEffect, useState} from "react";
import {toast} from "react-toastify";

export function Carts(){
    const params=useParams()
    const navigate = useNavigate()
    const [cart, setCart] = useState([])
    const getData = () =>{
        axios.get("http://localhost:9087/viewcart/"+params.userid)
            .then(response => {
                console.log(response)
                setCart(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])

    const ordernow  = (e) => {
        e.preventDefault()
      console.log("reached")
        navigate("/ordernow/"+params.userid)
    }
    return(
        <div>
                    {
                        cart.length>0
                            ? cart.map((item)=>
                                <div>
                                    <img align='left' style = {{margin:'0px', height: '200px' , width: '200px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}/>
                                    <form style={{height:'100px' ,width:'400px'}} align='right'>
                                        <label>Name: </label>            <input value={item.item_name} type="text"/><br/><br/><br/>
                                        <label>Description:</label>         <textarea>{item.description}</textarea><br/><br/><br/>
                                        <label>Price: </label>        <input value={item.price} type="text"/><br/><br/><br/>
                                        <label>Ordered Quantity: </label>         <input value={params.qty} type="text"/><br/><br/><br/>
                                        <label>Order Total : </label>         <input value={params.qty * item.price} type="text"/><br/><br/><br/>
                                    </form><br/><br/>
                                    <button className="btn_cls">
                                        <img align='left' style = {{right:'0px' , height: '150px' , width: '150px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  />
                                        <h3 style = {{right:'0px', textAlign:'center', fontSize:'10px' ,color:'black'}}>
                                            <p>Name: {item.item_name}<br/></p>
                                            <p>Description: {item.description} <br/></p>
                                            <p>Price: {item.price}</p>
                                            <p>Available Quantity: {item.qty}</p>
                                        </h3>
                                    </button><br/><br/>
                                </div>

                            )
                            :""
                    }
        </div>

    )
}
