import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import axios from "axios";
export function ViewOrdersToDeliveryBoy(){
    const [avaOrd , setAvaOrd] = useState([])
    const params= useParams()
    const [loc, setLocation] = useState()
    const getData = () =>{
        let token=JSON.parse(window.localStorage.getItem('Token'))
        axios.get("http://localhost:9087/delivery/viewordersatdeliverylocation/"+params.pincode,{
            headers: {'Authorization' : 'Bearer '+token,
                'Access-Control-Allow-Origin': '*'}
        } )
            .then(response => {
                console.log(response)
                setAvaOrd(response.data)
            })
            .catch(err => {
                console.log(err)
            })
    }
    useEffect(()=>{
        getData()
    },[])
    return(
        <>
                {
                    avaOrd.length>0
                        ? avaOrd.map((item)=>
                            <button className="btn_class_del">
                                <p align="left" style={{fontSize:'23px'}}>
                                    <br/><label style={{fontColor:'black',fontFamily:'Georgia'}}><h2>Customer Details:</h2><br/> </label>
                                    <label style={{fontColor:'black',fontFamily:'Gill Sans'}}>{item.customer.name} </label>
                                    <label style={{fontColor:'black',fontFamily:'Georgia'}}> {item.customer.phone}</label>
                                    <label style={{fontColor:'black',fontFamily:'Georgia'}}>{item.customer.email}</label>
                                    <br/><label style={{fontColor:'black',fontFamily:'Georgia'}}>{item.address.area} , {item.address.city} {item.address.state} {item.address.pincode}</label><br/>
                                    <br/><label style={{fontColor:'black',fontFamily:'Georgia'}}> </label><br/>
                                    <br/><label style={{fontColor:'black',fontFamily:'Georgia'}}>{item.partner.storeName} , {item.partner.storeLoc} ,{item.partner.contactno} {item.partner.email}</label><br/><br/>
                                </p>
                                <button type="submit">Pick</button>
                            </button>
                        )
                        :""
                }

        </>

    )
}
