import React, {useEffect} from "react";
import {Button, Col, Container, Row, Badge} from 'reactstrap';
import * as toast from "react-dom/test-utils";

export function checkthis(){
    const routeChange=()=> {
        var type = localStorage.getItem("type");
        let path = '';
        if(type == 1)
            path = '/Admin-Options';
        else
            path = '/Artists-Options';


    }

    const explore=()=>
    {
        let path = '/Explore';
        props.history.push(path)
    }

    const register=()=>
    {
        let path = '/Register';
        props.history.push(path);
    }


    const [credentials, setCredentials] = useState();
    const [artist,setArtist] = useState({});
    //form handler function
    const handleSubmit=(e)=>{
        console.log(credentials);
        postData(credentials);
        e.preventDefault();

    };

    //creating function to post data
    const postData=(data)=>{
        axios.post(`${base_url}/login`,data).then(
            (response)=>{
                console.log(response);
                console.log(response.data);
                if(response.data == ""){
                    toast.error("Invalid username and password, No such user exists",{
                        position : "bottom-center",
                    });}
                else if(response.data.type === 1){
                    localStorage.setItem('type',1);
                    toast.success("Admin Logged in!",{
                        position: "bottom-center",
                    });
                    routeChange();
                }
                else if(response.data.type === 2) {
                    localStorage.setItem('type', 2);
                    localStorage.setItem('email', response.data.username);
                    // localStorage.setItem('id',response.data.artist_id);
                    toast.success("Artist Logged in!",{
                        position: "bottom-center",
                    });
                    routeChange();
                    // localStorage.setItem('id', response.data.artist_id);
                    //  var email = localStorage.getItem("username")
                    //  axios.get( `${base_url}/artists/${email}`).then(
                    //   (response)=>{
                    //     console.log(response.data);
                    //     setArtist(response.data);
                    //     console.log(artist);
                    // },
                    // (error)=>{
                    //     console.log(error);
                    // }
                    // )
                }
                // else if(response.data.type == "undefined"){
                //   toast.error("Invalid username and password, No such user exists");
                // }
            },(error)=>{
                console.log(error);
                toast.error("Something went wrong!",{
                    position: "bottom-center",
                })
            }
        )
    };


    return(<div >
        <Container styles={{ backgroundColor:'black' }} >
            <h2 className='text-center mt-5 '>Welcome</h2>
            <h3 className='text-center '>To</h3>
            <h1 className='text-center mt-2' ><Badge color="primary"> The Gift Library</Badge></h1>

            <Row >

                <Col sm='6'>

                    <img src = {gb} style= {{ width:'66%', height:'60%', marginTop: '10%'}}/> <br/>

                    <Button color="info" onClick={explore} style={{width:'60%', height:'8%', marginTop: '5%'}} >Explore</Button><br/>
                    <Button color="warning" onClick={register} style={{width:'60%', height:'8%', marginTop: '5%'}} >Register</Button>
                </Col>
                <Col sm = '6'>

                    <img src={avatar} style= {{ width:'80%', height:'80%', marginTop: '5%'}}/>

                    <input
                        type="text"
                        placeholder=" Username"
                        onChange={(e) => setCredentials({...credentials,username:e.target.value})}
                        style={{marginLeft: '20%'}}/>

                    <input
                        type="password"
                        placeholder="Password"
                        onChange={(e) => setCredentials({...credentials,password:e.target.value})}
                        style={{marginLeft: '20%', marginTop: '2%'}}/><br/>

                    <Button color="success" onClick={handleSubmit} style={{width: '20%', marginLeft: '29%', marginTop: '5%'}}> Login </Button>

                </Col>
            </Row>
        </Container>

    </div>)
}
