import React, {useState} from "react";
import {useNavigate} from "react-router-dom";
import axios from "axios";
export function SearchShops(props){
    const [loc, setLocation] = useState()
    const navigate =useNavigate()
    const handleResponse= (e) =>{
        e.preventDefault()
        setLocation({...loc, type:"sweet"})
        console.log(loc.type)
        axios.post("http://localhost:9087/viewshops",loc)
            .then(response => {
                console.log(response)
                console.log(response.data)
                if(response.data !== "")
                    navigate("")
            })
            .catch(err => {
                console.log(err)
            })
    }
    return(
        <div style={{left:'0px'}}>
        <form   onSubmit={handleResponse}>
            <div>
                <label>Choose location : </label>
            </div><br/>
            <div><input type="text" placeholder="Enter location" onChange={(e)=>{setLocation({...loc, location:e.target.value})}}/>
            </div><br/>
            <div><input type="submit" value="Submit" onSubmit={handleResponse}/></div>
        </form>
        </div>
    )
}
