    }

    return (
        <div style={{ display: 'block',
            width: 700, padding: 30 }}>
            <label>Choose Address: </label>

            {
                address.length > 0 ?
                    address.map((add)=>

                        <Container
                            style={{
                                backgroundColor: 'green'
                            }}
                        >
                            <h3>{add.area}</h3>
                            <h3>{add.city}</h3>
                            <h3>{add.state}</h3>
                        </Container>
                    )
                    : <h2>No address found</h2>
            }
            <input type="button" onClick={addAddress}>Add new address</input>
        </div>
    );
}
