import React, {useEffect, useState} from "react"
import axios from "axios";
import {useNavigate} from "react-router-dom";
import Footer from "../Footer/Footer";
import {Logout} from "../Logout";

export function CheckPostGet( ){
    const [user, setUser] = useState([])
    const navigate = useNavigate()

    Logout = () =>{
        localStorage.removeItem('Token');
        navigate('/')
    }

    const getData = () => {
        let id= JSON.parse(window.localStorage.getItem('ID'))
        let token=JSON.parse(window.localStorage.getItem('Token'))
        console.log(id)
        console.log(token)

        axios.get("http://localhost:9087/customer/"+id ,{
          headers: {'Authorization' : 'Bearer '+token,
          'Access-Control-Allow-Origin': '*'}
        })
            .then(response => {
                console.log(response.data)
                setUser(response.data)
            })
            .catch(err => {
                console.log('error')
                console.log(err)
            })
    }

    useEffect(()=>{
        getData()
    },[])
    return(
        <>
            <p align="right">
                <button style={{marginRight:'6px'}} className="desc" >Add to Cart</button>
                <button style={{marginRight:'6px'}} className="desc" >Buy now</button>
            </p>
            <img align="left" src={user.image} alt="Profile"
                style={{height:'200px', weight:'170px'}}/><br/><br/>
            <p align="right" style={{fontStyle:'oblique'}}>
               <h1>{user.name}</h1> <br/><br/>
                <h1>{user.email}</h1><br/><br/>
                <h1>{user.phone}</h1><br/><br/>
            </p>

            <label >
                <input className="logoutLblPos" name="submit2" type="submit" id="submit2" value="log out" onChange={Logout}/>
            </label>

        </>
    )
}
