import React, {useEffect, useState} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";
import {Container, Row} from "reactstrap";

export function Inventory(){

    const params = useParams()
    const [inventory, setInventory]= useState([])

    const getData = () =>{
    axios.get("http://localhost:9087/inventory/"+params.userid)
        .then(response => {
            console.log(response)
            setInventory(response.data)
        })
        .catch(err => {
            console.log(err)
        })
}
    useEffect(()=>{
        getData()
    },[])

    return(
        <div>
            <div className = 'text-center' >
                <h1>Products</h1>
            </div>
                    {
                        inventory.length > 0
                            ? inventory.map((item) =>
                            <div>
                                <button className="btn_cls">
                                    <img align='left' style = {{right:'0px' , height: '150px' , width: '150px'}} alt={item.item_name} src={`data:image/jpeg;base64,${item.image}`}  />
                                    <h3 style = {{right:'0px', textAlign:'right', fontSize:'10px'}}>
                                        <p>Name: </p>{item.item_name}<br/>
                                        <p>Description: </p>{item.description} <br/>
                                        <p>Price: </p>{item.price}</h3>
                                </button><br/><br/>
                            </div>

                            )
                            : "No products"
                    }>

        </div>
    )

}
